import { useImperial } from "../contexts/ImperialContext";

export function LongtermForecast({ children, data }) {
    const { imperial } = useImperial();

    function calcTemp() {
        const tempVar = Math.round(data?.main?.temp - 273);
        return imperial
            ? Math.round(tempVar * 1.8 + 32) + "째F"
            : tempVar + "째C";
    }

    return (
        <>
            <tr>
                {/* DATETIME */}
                <td className="longterm-wthr-cell">
                    {data?.dt_txt?.slice(0, 10)} <br />
                    {data?.dt_txt?.slice(10, 16)}
                </td>
                {/* WEATHER DATA */}
                <td className="longterm-wthr-data longterm-wthr-cell">
                    {calcTemp()}
                    <img
                        src={`https://openweathermap.org/img/wn/${data?.weather[0]?.icon}@2x.png`}
                        alt={data?.weather[0]?.description}
                    />
                </td>
            </tr>
            {/* SEPARATOR */}
            <tr>
                <td colSpan={2}>
                    <hr />
                </td>
            </tr>
        </>
    );
}
