import { useEffect, useState } from "react";
import AddToFavourites from "./AddToFavourites";
import API_KEY from "../API_KEY";

function CityDetails({
    children,
    city = "Wroc≈Çaw",
    temperature = "20",
    citiesState,
    onClick,
}) {
    const [showDetails, setShowDetails] = useState(false);
    const [errorCurrent, setErrorCurrent] = useState("");
    const [errorLongterm, setErrorLongterm] = useState("");
    const [loadingCurrent, setLoadingCurrent] = useState(false);
    const [loadingLongterm, setLoadingLongterm] = useState(false);
    const [currentWeather, setCurrentWeather] = useState([]);
    const [longtermWeather, setLongtermWeather] = useState([]);

    function handleShowDetails() {
        setShowDetails(!showDetails);
    }

    function closeCityForecast(e) {
        if (e.key === "Escape") {
            setShowDetails(false);
        }
    }

    function calcWind(deg) {
        let direction;
        if (deg > 337.5 && deg < 22.5) {
            direction = "‚¨á";
        } else if (deg > 22.5 && deg < 67.5) {
            direction = "‚Üô";
        } else if (deg > 67.5 && deg < 112.5) {
            direction = "‚¨Ö";
        } else if (deg > 112.5 && deg < 157.5) {
            direction = "‚Üñ";
        } else if (deg > 157.5 && deg < 202.5) {
            direction = "‚¨Ü";
        } else if (deg > 202.5 && deg < 247.5) {
            direction = "‚Üó";
        } else if (deg > 247.5 && deg < 292.5) {
            direction = "‚û°";
        } else if (deg > 292.5 && deg < 337.5) {
            direction = "‚Üò";
        }

        return direction;
    }

    document.addEventListener("keydown", closeCityForecast);

    useEffect(function () {
        async function fetchCurrent() {
            try {
                setLoadingCurrent(true);
                setErrorCurrent("");
                const res = await fetch(
                    `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY()}`
                );

                if (!res.ok) throw new Error("Fetching movies went wrong");

                const data = await res.json();
                if (data.Response === "False")
                    throw new Error(
                        "Coudln't fetch current weather conditions"
                    );

                setCurrentWeather(data);
                console.log(data);

                setLoadingCurrent(false);
                setErrorCurrent("");
            } catch (err) {
                console.error(err);
                setErrorCurrent(err.message);
            } finally {
                setLoadingCurrent(false);
            }
        }

        async function fetchLongterm() {
            try {
                setLoadingLongterm(true);
                setErrorLongterm("");
                const res = await fetch(
                    `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY()}`
                );

                if (!res.ok) throw new Error("Fetching movies went wrong");

                const data = await res.json();
                if (data.Response === "False")
                    throw new Error("Couldn't fetch longterm weather forecast");

                setLongtermWeather(data.Search);
                setLoadingLongterm(false);
                setErrorLongterm("");
            } catch (err) {
                console.error(err);
                setErrorLongterm(err.message);
            } finally {
                setLoadingLongterm(false);
            }
        }

        fetchCurrent();
    }, []);

    return (
        <div className="city-container">
            <h3>{city}</h3>
            <AddToFavourites
                cities={citiesState}
                city={city}
                onClick={onClick}
            />
            <div className="city-details">
                {loadingCurrent && "≈Åadowanie..."}
                {!loadingCurrent && !errorCurrent && (
                    <>
                        <table>
                            <tbody>
                                {/* TEMPERATURE */}
                                <tr>
                                    <td className="item-right">
                                        üå°{" "}
                                        {Math.round(
                                            currentWeather?.main?.temp - 273
                                        )}
                                        ¬∞C
                                    </td>
                                </tr>
                                <tr>
                                    {/* WEATHER ICON */}
                                    <td>
                                        <img
                                            src={`https://openweathermap.org/img/wn/${currentWeather?.weather[0]?.icon}@2x.png`}
                                            alt={
                                                currentWeather?.weather[0]?.icon
                                            }
                                        />
                                    </td>
                                </tr>
                                {/* RAINFALL */}
                                <tr>
                                    <td>üåß 21.37%, 21.37mm/m2</td>
                                </tr>
                                {/* WIND */}
                                <tr>
                                    <td>
                                        üí® {currentWeather?.wind?.speed} km/h{" "}
                                        {calcWind(currentWeather?.wind?.deg)}
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        {showDetails && (
                            <table>
                                <thead>
                                    <th>5-dniowa prognoza</th>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>5day forecast</td>
                                    </tr>
                                </tbody>
                            </table>
                        )}
                    </>
                )}
            </div>

            {/* dynamically choose which icon to show, arrow down if rolled up, arrow up if rolled down */}
            {showDetails ? (
                <svg
                    onClick={handleShowDetails}
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-chevron-compact-up"
                    viewBox="0 0 16 16">
                    <path
                        fillRule="evenodd"
                        d="M7.776 5.553a.5.5 0 0 1 .448 0l6 3a.5.5 0 1 1-.448.894L8 6.56 2.224 9.447a.5.5 0 1 1-.448-.894z"
                    />
                </svg>
            ) : (
                <svg
                    onClick={handleShowDetails}
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-chevron-compact-down"
                    viewBox="0 0 16 16">
                    <path
                        fillRule="evenodd"
                        d="M1.553 6.776a.5.5 0 0 1 .67-.223L8 9.44l5.776-2.888a.5.5 0 1 1 .448.894l-6 3a.5.5 0 0 1-.448 0l-6-3a.5.5 0 0 1-.223-.67"
                    />
                </svg>
            )}
        </div>
    );
}

export default CityDetails;
