import { createContext, useContext, useState } from "react";

const ImperialContext = createContext();

const getLocalStorageItem = (key, defaultValue) => {
    const item = localStorage.getItem(key);
    if (item === null) {
        localStorage.setItem(key, JSON.stringify(defaultValue));
        return defaultValue;
    }
    return JSON.parse(item);
};

function ImperialProvider({ children }) {
    const [imperial, setImperial] = useState(
        getLocalStorageItem("imperial", false)
    );

    function handleImperialChange() {
        const newImperial = !imperial;
        setImperial(newImperial);
        localStorage.setItem("imperial", JSON.stringify(newImperial));
    }

    function calcTemp(data) {
        const tempVar = Math.round(data?.main?.temp - 273);
        return imperial
            ? Math.round(tempVar * 1.8 + 32) + "°F"
            : tempVar + "°C";
    }

    return (
        <ImperialContext.Provider
            value={{ imperial, handleImperialChange, calcTemp }}>
            {children}
        </ImperialContext.Provider>
    );
}

function useImperial() {
    const context = useContext(ImperialContext);
    if (context === undefined) {
        throw new Error("ImperialContext użyte poza ImperialProvider");
    }

    return context;
}

export { ImperialProvider, useImperial };
