import { useEffect, useReducer, useState } from "react";
import AddToFavourites from "./AddToFavourites";
import API_KEY from "../API_KEY";
import { ShowMoreButton } from "./ShowMoreButton";
import { LongtermForecast } from "./LongtermForecast";

const initialState = {
    showDetails: false,
    errorCurrent: "",
    errorLongterm: "",
    loadingCurrent: false,
    loadingLongterm: false,
    currentWeather: [],
    longtermWeather: [],
    imageError: false,
};

function calcWind(deg) {
    if (deg === undefined) return "X";
    if (deg > 337.5 || deg <= 22.5) {
        return "‚¨á";
    } else if (deg > 22.5 && deg <= 67.5) {
        return "‚Üô";
    } else if (deg > 67.5 && deg <= 112.5) {
        return "‚¨Ö";
    } else if (deg > 112.5 && deg <= 157.5) {
        return "‚Üñ";
    } else if (deg > 157.5 && deg <= 202.5) {
        return "‚¨Ü";
    } else if (deg > 202.5 && deg <= 247.5) {
        return "‚Üó";
    } else if (deg > 247.5 && deg <= 292.5) {
        return "‚û°";
    } else if (deg > 292.5 && deg <= 337.5) {
        return "‚Üò";
    }
}

function reducer(state, action) {
    switch (action.type) {
        case "current/loading":
            return { ...state, loadingCurrent: true, errorCurrent: "" };

        case "current/loaded":
            return { ...state, loadingCurrent: false };

        case "longterm/loading":
            return { ...state, loadingLongterm: true, errorLongterm: "" };

        case "longterm/loaded":
            return { ...state, loadingLongterm: false };

        case "image/error":
            return { ...state, imageError: true };

        case "details/toggle":
            return { ...state, showDetails: !state.showDetails };

        case "details/hidden":
            return { ...state, showDetails: action.payload };

        case "currentData/loaded":
            return { ...state, currentWeather: action.payload };

        case "longtermData/loaded":
            return { ...state, longtermWeather: action.payload };

        case "rejected":
            return { ...state, error: action.payload, loading: false };

        default:
            throw new Error("Operacja nieznana, CityDetails.js");
    }
}

function CityDetails({ children, city = "Wroc≈Çaw", onClick, imperial }) {
    // const [showDetails, setShowDetails] = useState(false);
    // const [errorCurrent, setErrorCurrent] = useState("");
    // const [errorLongterm, setErrorLongterm] = useState("");
    // const [loadingCurrent, setLoadingCurrent] = useState(false);
    // const [loadingLongterm, setLoadingLongterm] = useState(false);
    // const [currentWeather, setCurrentWeather] = useState([]);
    // const [longtermWeather, setLongtermWeather] = useState([]);
    // const [imageError, setImageError] = useState(false);

    const [
        {
            showDetails,
            errorCurrent,
            errorLongterm,
            loadingCurrent,
            loadingLongterm,
            currentWeather,
            longtermWeather,
            imageError,
        },
        dispatch,
    ] = useReducer(reducer, initialState);

    function handleShowDetails() {
        dispatch({ type: "details/toggle" });
    }

    function closeCityForecast(e) {
        if (e.key === "Escape") {
            dispatch({ type: "details/toggle", payload: false });
        }
    }

    function calcTemp() {
        const tempVar = Math.round(currentWeather?.main?.temp - 273);
        return imperial
            ? Math.round(tempVar * 1.8 + 32) + "¬∞F"
            : tempVar + "¬∞C";
    }

    useEffect(
        function () {
            async function fetchCurrent() {
                dispatch({ type: "current/loading" });
                try {
                    const res = await fetch(
                        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY()}`
                    );

                    if (!res.ok)
                        throw new Error(
                            "Fetching current weather conditions went wrong"
                        );

                    const data = await res.json();
                    if (data.Response === "False")
                        throw new Error(
                            "Coudln't fetch current weather conditions"
                        );

                    dispatch({ type: "currentData/loaded", payload: data });
                } catch (err) {
                    console.error(err);
                    setErrorCurrent(err.message);
                } finally {
                    dispatch({ type: "current/loaded" });
                }
            }

            async function fetchLongterm() {
                dispatch({ type: "longterm/loading" });
                try {
                    const res = await fetch(
                        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY()}`
                    );

                    if (!res.ok)
                        throw new Error(
                            "Fetching longterm forecast went wrong"
                        );

                    const data = await res.json();
                    if (data.Response === "False")
                        throw new Error(
                            "Couldn't fetch longterm weather forecast"
                        );

                    dispatch({ type: "longtermData/loaded", payload: data });
                } catch (err) {
                    console.error(err);
                    setErrorLongterm(err.message);
                } finally {
                    dispatch({ type: "longterm/loaded" });
                }
            }

            document.addEventListener("keydown", closeCityForecast);

            fetchCurrent();
            fetchLongterm();

            return function () {
                document.removeEventListener("keydown", closeCityForecast);
            };
        },
        [city]
    );

    return (
        <div className="city-container">
            <h3>{city}</h3>
            <AddToFavourites city={city} onClick={onClick} />
            <div className="city-details">
                {loadingCurrent && "≈Åadowanie..."}
                {!loadingCurrent && !errorCurrent && (
                    <>
                        <table>
                            <tbody>
                                {/* TEMPERATURE */}
                                <tr>
                                    <td className="item-right">
                                        üå° {calcTemp()}
                                    </td>
                                </tr>
                                <tr>
                                    {/* WEATHER ICON */}
                                    <td className="text-center">
                                        <WeatherIcon
                                            iconCode={
                                                currentWeather?.weather?.[0]
                                                    ?.icon
                                            }
                                            description={
                                                currentWeather?.weather?.[0]
                                                    ?.description
                                            }
                                        />
                                    </td>
                                </tr>
                                {/* RAINFALL */}
                                <tr>
                                    <td>
                                        üåß {currentWeather?.rain?.["1h"] || "0"}
                                        mm/m2, 21.37%
                                    </td>
                                </tr>
                                {/* WIND */}
                                <tr>
                                    <td>
                                        üí® {currentWeather?.wind?.speed || "0"}{" "}
                                        km/h{" "}
                                        {calcWind(currentWeather?.wind?.deg)}
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        {showDetails && (
                            <ShowMoreButton
                                showDetails={showDetails}
                                onShowDetails={handleShowDetails}
                            />
                        )}
                        {showDetails && (
                            <>
                                {loadingLongterm && "≈Åadowanie..."}
                                {!loadingLongterm && !errorLongterm && (
                                    <table>
                                        <tbody>
                                            {longtermWeather?.list?.map(
                                                (elem) => (
                                                    <LongtermForecast
                                                        data={elem}
                                                        key={elem?.dt_txt}
                                                        imperial={imperial}
                                                    />
                                                )
                                            )}
                                        </tbody>
                                    </table>
                                )}
                            </>
                        )}
                    </>
                )}
            </div>

            <ShowMoreButton
                showDetails={showDetails}
                onShowDetails={handleShowDetails}
            />
        </div>
    );
}

const WeatherIcon = ({ iconCode, description }) => {
    const [hasError, setHasError] = useState(false);

    const handleError = () => {
        setHasError(true);
    };

    const iconUrl =
        hasError || !iconCode
            ? "https://openweathermap.org/img/wn/04n@2x.png"
            : `https://openweathermap.org/img/wn/${iconCode}@2x.png`;

    return (
        <img
            src={iconUrl}
            alt={description || "weather icon"}
            onError={handleError}
            className="weather-icon"
            loading="lazy"
        />
    );
};

export default CityDetails;
