import CityDetails from "../components/CityDetails";
import Header from "../components/Header";

function Forecast({ children, cities, onClick, imperial, onCityLookup }) {
    document.title = "Prognoza";

    const [suggestions, setSuggestions] = useState([]);
    const [load, setLoad] = useState(false);
    useEffect(function () {
        async function nazwa(params = "lub") {
            try {
                const response = await fetch(
                    `https://api.openweathermap.org/geo/1.0/direct?q=${params}&limit=5&appid=${API_KEY()}`
                );
                setLoad(true);
                if (!response.ok) {
                    throw new Error("Błąd pobierania sugestii");
                }

                const data = await response.json();
                const formattedSuggestions = data.map((city) => ({
                    name: city.name,
                    country: city.country,
                    state: city.state,
                    lat: city.lat,
                    lon: city.lon,
                }));

                setSuggestions(formattedSuggestions);
            } catch (err) {
                console.error("Błąd:", err);
                setSuggestions([]);
                setLoad(false);
            }
        }

        nazwa();
    }, []);

    return (
        <div>
            <Header />
            <div className="city-lookup">
                <form onSubmit={onCityLookup}>
                    <input type="text" placeholder="Wpisz miasto" />
                </form>
            </div>
            <div className="forecast-container">
                {!load &&
                    suggestions.map((suggestion) => {
                        return <h1>{suggestion}</h1>;
                    })}
                {/* {cities.map((city) => {
                    return (
                        <CityDetails
                            city={city}
                            key={city}
                            onClick={onClick}
                            imperial={imperial}
                        />
                    );
                })} */}
            </div>
        </div>
    );
}

export default Forecast;
