import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./styles/Global.css";
import "./styles/Computer.css";
import "./styles/Mobile.css";
// import Placeholder from "./components/Placeholder";
import Footer from "./components/Footer";
import Homepage from "./pages/Homepage";
import Favourites from "./pages/Favourites";
import Forecast from "./pages/Forecast";

function App() {
    document.title = "Prognoza Pogody";

    // placeholder cities
    const initialCities = JSON.stringify([
        "Legnica",
        "Wrocław",
        "Poznań",
        "Opole",
        "Zielona Góra",
    ]);

    // if saved cities list is empty, place in a list of placeholder cities
    !localStorage.getItem("fav_cities") &&
        localStorage.setItem("fav_cities", initialCities);

    const saved = JSON.parse(localStorage.getItem("fav_cities"));

    return (
        <div className="App">
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Homepage />}></Route>
                    <Route
                        path="/prognoza"
                        element={<Forecast cities={saved} />}></Route>
                    <Route
                        path="/ulubione-miasta"
                        element={<Favourites />}></Route>
                </Routes>
            </BrowserRouter>
            <Footer />
        </div>
    );
}

export default App;

