import CityDetails from "../components/CityDetails";
import Header from "../components/Header";

function Forecast({ children, cities }) {
    const [citiesState, setCitiesState] = useState(cities);
    function handleCity(elem) {
        console.log(elem);
        if (cities.includes(elem)) {
            setCitiesState(citiesState.filter((cty) => cty !== elem));
            localStorage.setItem(
                "fav_cities",
                JSON.stringify(citiesState.filter((ct) => ct !== elem))
            );
        } else {
            setCitiesState([...citiesState, elem]);
            localStorage.setItem(
                "fav_cities",
                JSON.stringify([...citiesState, elem])
            );
        }
    }

    return (
        <div>
            <Header />
            <div className="forecast-container">
                {cities.map((city) => {
                    return (
                        <CityDetails
                            city={city}
                            cities={cities}
                            key={city}
                            onClick={handleCity}
                        />
                    );
                })}
            </div>
        </div>
    );
}

export default Forecast;
