import { useState } from "react";
import CityDetails from "../components/CityDetails";
import Header from "../components/Header";

function Forecast({ children, cities }) {
    document.title = "Prognoza";

    const [citiesState, setCitiesState] = useState(cities);
    function handleCity(elem) {
        console.log(elem);
        if (citiesState.includes(elem)) {
            console.log("includes");
            setCitiesState(citiesState.filter((cty) => cty !== elem));
            localStorage.setItem(
                "fav_cities",
                JSON.stringify(citiesState.filter((ct) => ct !== elem))
            );
        } else if (!citiesState.includes(elem)) {
            console.log("not includes");
            setCitiesState([
                ...JSON.parse(localStorage.getItem("fav_cities")),
                elem,
            ]);
            localStorage.setItem(
                "fav_cities",
                JSON.stringify([
                    ...JSON.parse(localStorage.getItem("fav_cities")),
                    elem,
                ])
            );
        }
    }

    return (
        <div>
            <Header />
            <div className="forecast-container">
                {cities.map((city) => {
                    return (
                        <CityDetails
                            city={city}
                            cities={citiesState}
                            key={city}
                            citiesState={citiesState}
                            onClick={handleCity}
                        />
                    );
                })}
            </div>
        </div>
    );
}

export default Forecast;
